import React, { useEffect, useState } from "react";
import "../Home.css";

export default function Home(props) {
  const userUid = localStorage.getItem("userUid");
  const [totalStudentsHave, setTotalStudentsHave] = useState(0);
  useEffect(() => {
    props.user.forEach((item) => {
      if (item.uid === userUid) {
        setTotalStudentsHave(item.totalStudents);
      }
    });
  });

  const [students, setStudent] = useState([]);
  useEffect(() => {
    fetch("/student")
      .then((res) => {
        return res.json();
      })
      .then((data) => {
        setStudent(data.student);
      });
  }, []);

  async function Handler1() {
    const res = await fetch("http://localhost:5000/updateStudentCountTeacher", {
      method: "POST",
      mode: "cors",
      body: JSON.stringify({ uid: userUid }),
      headers: {
        "Content-Type": "application/json",
      },
    });

    if (res.status === 201) {
      const val = await res.json();
      setTotalStudentsHave(val);
      console.log(val)
    }
  }

  async function Handler(name) {
    console.log("Hello");
    const res = await fetch("http://localhost:5000/updateStudent", {
      method: "POST",
      mode: "cors",
      body: JSON.stringify({ name: name }),
      headers: {
        "Content-Type": "application/json",
      },
    });
    if (res.status === 200) {
      const val = await res.json();
      setStudent(val.student);
    }
  }
  return (
    <div>
      <div id="div1">
        <p id="noStudentP">All students are assigned</p>
        <div id="grid">
          {students.map((value, index) => {
            if (value.assigned == 0) {
              document.getElementById("noStudentP").style.display = "none";
              return (
                <div className="gridDiv">
                  <p className="nameP">{value.name}</p>
                  <button
                    onClick={() => {
                      if(totalStudentsHave < 4){
                        Handler(value.name);
                        Handler1();
                      }
                      else{
                        alert("Can't add more");
                      }
                    }}
                  >
                    Take
                  </button>
                </div>
              );
            }
          })}
        </div>
      </div>
      <div id="div2">
        <p id="noStudentTeP">No student assigned to you</p>
        <div id="grid"></div>
      </div>
    </div>
  );
}
