import React, { useEffect, useState } from "react";
import "../Home.css";

export default function Home() {
  async function updateHandler(name) {
    const res = await fetch("/updateStudent", {
      method: "POST",
      mode: "cors",
      body: JSON.stringify({name: name}),
      headers: {
        "Content-Type": "application/json",
      },
    });
    if (res.status === 200) {
      const val = await res.json();
      setStudent(val);
    }
  }

  const [students, setStudent] = useState([]);
  const userUid = localStorage.getItem("userUid");

  useEffect(() => {
    fetch("/student")
      .then((res) => {
        return res.json();
      })
      .then((data) => {
        setStudent(data.student);
      });
  }, []);

  return (
    <div>
      <div id="div1">
        <p id="noStudentP">All students are assigned</p>
        <div id="grid">
          {students.map((value, index) => {
            if (value.assigned === "0") {
              document.getElementById("noStudentP").style.display = "none";
              return (
                <div className="gridDiv">
                  <p className="nameP">{value.name}</p>
                  <button onClick={() => {updateHandler(value.name)}}>Take</button>
                </div>
              );
            }
          })}
        </div>
      </div>
      <div id="div2">
        <p id="noStudentTeP">No student assigned to you</p>
        <div id="grid">
          
        </div>
      </div>
    </div>
  );
}
