import React, { useEffect, useState } from "react";
import "../Home.css";

async function handler()
{
    const res=await fetch("request route example localhost 5000/update", 
    {
        method: "POST",
        mode:'cors',
        body: JSON.stringify({marks:"10",name:"student1"}),
        headers: {
        "Content-Type": "application/json",
        }
    })
    if(res.status===200) //response code sent by backend
    {
        const val=await res.json();
        setStudentMarks(val);
    }
}

export default function Home() {
  const [students, setStudent] = useState([]);
  const userUid = localStorage.getItem("userUid");

  useEffect(() => {
    fetch("/student")
      .then((res) => {
        return res.json();
      })
      .then((data) => {
        setStudent(data.student);
      });
  }, []);

  return (
    <div>
      <div id="div1">
        <p id="noStudentP">All students are assigned</p>
        <div id="grid">
          {students.map((value, index) => {
            if (value.assigned == "0") {
              document.getElementById("noStudentP").style.display = "none";
              return (
                <div className="gridDiv">
                  <p className="nameP">{value.name}</p>
                  <button onClick={() => {}}>Take</button>
                </div>
              );
            }
          })}
        </div>
      </div>
      <div id="div2">
        <p id="noStudentTeP">No student assigned to you</p>
        <div id="grid">
          {students.map((value, index) => {
            if (value.assigned == "0") {
              document.getElementById("noStudentTeP").style.display = "none";
              return (
                <div className="gridDiv">
                  <p className="nameP">{value.name}</p>
                  <button onClick={() => {}}>Take</button>
                </div>
              );
            }
          })}
        </div>
      </div>
    </div>
  );
}
