import React, { useEffect, useState } from "react";
import "../Home.css";

export default function Home(props) {
  const userUid = localStorage.getItem("userUid");

  async function Handler(name) {
    console.log("Hello")
    const res = await fetch("http://localhost:5000/updateStudent", {
      method: "POST",
      mode: "cors",
      body: JSON.stringify({name: name}),
      headers: {
        "Content-Type": "application/json",
      },
    });
    if (res.status === 200) {
      const val = await res.json();
      console.log()
    }
  }
  return (
    <div>
      <div id="div1">
        <p id="noStudentP">All students are assigned</p>
        <div id="grid">
          {props.students.map((value, index) => {
            if (value.assigned === "0") {
              document.getElementById("noStudentP").style.display = "none";
              return (
                <div className="gridDiv">
                  <p className="nameP">{value.name}</p>
                  <button onClick={() => {Handler(value.name)}}>Take</button>
                </div>
              );
            }
          })}
        </div>
      </div>
      <div id="div2">
        <p id="noStudentTeP">No student assigned to you</p>
        <div id="grid">
          
        </div>
      </div>
    </div>
  );
}
