import React, { useState, useEffect } from "react";
import "../Authentication.css";

export default function Authentication(props) {
  const [pin, setPin] = useState("");
  const userUid_1 = localStorage.getItem("userUid");
  let originalPin = ""
  let [userPin, setUserPin] = useState("");

  useEffect(() => {
    {
      props.user.map((value, index) => {
        if (value.uid == userUid_1) {
          originalPin = userPin
          userPin = value.pin;
        }
      });
    }
  }, "");

  return (
    <div class="container">
      <form>
        <input
          type="text"
          placeholder="Enter your pin"
          value={pin}
          onChange={(e) => setPin(e.target.value)}
        />
        <br />
        <button
          type="submit"
          onClick={() => {
            console.log(pin, ", ", userPin)
            if (pin === userPin) {
              alert("Looks Good");
            } else {
              alert("Opps!");
            }
          }}
        >
          Submit
        </button>
      </form>
    </div>
  );
}
