import React, { useEffect, useState } from "react";
import "./App.css";
import {useNavigate} from ''

function App() {
  const [user, setUser] = useState([]);

  useEffect(() => {
    fetch("/api")
      .then((res) => {
        return res.json();
      })
      .then((data) => {
        setUser(data.employees);
      });
  }, []);

  return (
    <>
      <div id="grid">
        {user.map((value, index) => {
          return (
            <>
              <div className="gridDiv">
                <h4>{value.name}</h4>
                <h5>{value.uid}</h5>
              </div>
            </>
          );
        })}
      </div>
    </>
  );
}

export default App;
