import React, { useEffect, useState } from "react";
import "../Home.css";
import "../Authentication.css";
import { Link } from "react-router-dom";

export default function TeacherPage() {
  const [takeList, setTake] = useState([]);
  const userUid = localStorage.getItem("userUid");

  async function ReturnStudentList() {
    const res = await fetch("http://localhost:5000/returnStudentList", {
      method: "POST",
      mode: "cors",
      body: JSON.stringify({ uid: userUid }),
      headers: {
        "Content-Type": "application/json",
      },
    });
    let val;
    if (res.status === 200) {
      val = await res.json();
      setTake(val);
    }
  }

  useEffect(() => {
    ReturnStudentList();
  }, []);

  return (
    <div>
      <div id="div1">
        {
          <div id="grid">
            {Object.keys(takeList).map((keys) => {
              return (
                <div className="gridDiv">
                  <p className="stuName1">
                    {takeList[`${keys}`] != "" ? takeList[`${keys}`] : "No one"}
                  </p>
                  <button>Assign marks</button>
                </div>
              );
            })}
          </div>
        }
      </div>

      <div id="div2">
        <div className="container">
          <form>
            <input
              type="text"
              placeholder="Enter your pin"
              value={pin}
              id="inputPin"
              onChange={(e) => setPin(e.target.value)}
            />
            <br />
            <button
              type="button"
              id="submitBtn"
              onClick={() => {
                if (pin === userPin) {
                  alert("Looks Good");
                  {
                    document.getElementById("inputPin").style.display = "none";
                  }
                  {
                    document.getElementById("submitBtn").style.display = "none";
                  }
                  {
                    document.getElementById("nextPage").style.visibility =
                      "visible";
                  }
                } else {
                  alert("Opps!");
                }
              }}
            >
              Submit
            </button>
            <br />
            <Link to={"/home"}>
              <button type="button" id="nextPage">
                Next
              </button>
            </Link>
          </form>
        </div>
      </div>
    </div>
  );
}
