import React, { useEffect, useState } from "react";
import "./App.css";
import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom';

function App() {
  const [user, setUser] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    fetch("/api")
      .then((res) => {
        return res.json();
      })
      .then((data) => {
        setUser(data.employees);
      });
  }, []);

  return (
    <>
    <Switch>
          <Route path="/about">
            <AboutPage />
          </Route>
          <Route path="/">
            <HomePage />
          </Route>
        </Switch>
      <div id="grid">
        {user.map((value, index) => {
          return (
            <>
              <div className="gridDiv" onClick={() => {
                navigate("/authentication")
              }}>
                <h4>{value.name}</h4>
                <h5>{value.uid}</h5>
              </div>
            </>
          );
        })}
      </div>
    </>
  );
}

export default App;
