import React, { useEffect, useState } from "react";

function App() {
  const [user, setUser] = useState([]);

  useEffect(() => {
    fetch("/api")
      .then((res) => {
        return res.json();
      })
      .then((data) => {
        setUser(data.employees);
      });
  }, []);

  return (
    <div id="grid">
      {user.map((value, index) => {
        return (
          <>
            <div id="gridDiv">
              <h1>Name : {value.name}</h1>
              <h1>Uid : {value.uid}</h1>
            </div>
          </>
        );
      })}
    </div>
  );
}

export default App;
