import React, { useEffect, useState } from "react";
import "../Home.css";
import "../Authentication.css";
import { Link } from "react-router-dom";

export default function TeacherPage() {
  const [takeList, setTake] = useState([]);
  const userUid = localStorage.getItem("userUid");

  async function ReturnStudentList() {
    const res = await fetch("http://localhost:5000/returnStudentList", {
      method: "POST",
      mode: "cors",
      body: JSON.stringify({ uid: userUid }),
      headers: {
        "Content-Type": "application/json",
      },
    });
    let val;
    if (res.status === 200) {
      val = await res.json();
      setTake(val);
    }
  }

  useEffect(() => {
    ReturnStudentList();
  }, []);

  async function addMarks(totalMarks, name) {
    const res = await fetch("http://localhost:5000/updateMarks", {
      method: "POST",
      mode: "cors",
      body: JSON.stringify({ uid: userUid, name: name, totalMarks: totalMarks}),
      headers: {
        "Content-Type": "application/json",
      },
    });
    let val;
    if (res.status === 200) {
      val = await res.json();
      console.log(val);
    }
  }

  return (
    <div>
      <div id="div1">
        {
          <div id="grid">
            {Object.keys(takeList).map((keys) => {
              return (
                <div className="gridDiv">
                  <p className="stuName1">
                    {takeList[`${keys}`] != "" ? takeList[`${keys}`] : "No one"}
                  </p>
                  <button onClick={()=>{
                    let tMarks = 0
                    takeList[`${keys}`] != "" ? addMarks(100, takeList[`${keys}`]) : alert("No student assigned here")
                  }}>Assign marks</button>
                  <button>Final submit</button>
                </div>
              );
            })}
          </div>
        }
      </div>

      <div id="div2">
        <div className="container">
          <p>Give marks on parameters</p>
          <p>And click on assign marks of perticular student to whom you want to assign marks</p>
          <form>
            <input type="number" placeholder="Timeframe ..." />
            <input type="number" placeholder="Budget ..." />
            <input type="number" placeholder="Quality ..." />
            <input type="number" placeholder="Scope ..." />
            <input type="number" placeholder="Stakeholder satisfaction ..." />
            <input type="number" placeholder="Sustainability ..." />
            <br />
          </form>
        </div>
      </div>
    </div>
  );
}
