import React, { useEffect, useState } from "react";
import "./App.css"

function App() {
  const [user, setUser] = useState([]);

  let h4Style = {}

  useEffect(() => {
    fetch("/api")
      .then((res) => {
        return res.json();
      })
      .then((data) => {
        setUser(data.employees);
      });
  }, []);

  return (
    <div id="grid">
      {user.map((value, index) => {
        return (
          <>
            <div className ="gridDiv">
              <h4 style={h4Style}>
                {value.name}
              </h4>
              <h5>
                {value.uid}
              </h5>
            </div>
          </>
        );
      })}
    </div>
  );
}

export default App;
